mapper.select:
select paper.id, paper.headline, paper.deadline, paper.subject_id, paper.grade_id, paper.class_id_array, paper.user_id, paper.create_time, u.real_name from v_paper AS paper LEFT JOIN v_user AS u ON paper.user_id = u.id WHERE paper.del_flag = '0'
AND paper.grade_id = #{query.gradeId}
AND paper.subject_id = #{query.subjectId}
AND paper.headline LIKE CONCAT(CONCAT('%',#{query.headline},'%'))

select paper.id, paper.headline, paper.deadline, paper.subject_id, paper.grade_id, paper.class_id_array, paper.user_id, paper.create_time, answer.answer_state, answer.correct_state, u.real_name from v_paper AS paper INNER JOIN v_answer AS answer ON paper.id = answer.paper_id INNER JOIN v_user AS u ON paper.user_id = u.id WHERE paper.del_flag = '0' AND answer.del_flag = '0'
AND answer.user_id = #{query.userId}
AND paper.grade_id = #{query.gradeId}
AND paper.subject_id = #{query.subjectId}
AND FIND_IN_SET(paper.grade_id,#{query.gradeIds}) AND FIND_IN_SET(paper.subject_id,#{query.subjectIds})
AND paper.headline LIKE CONCAT(CONCAT('%',#{query.headline},'%'))
